document.addEventListener("DOMContentLoaded",function(){const t=document.querySelectorAll(".book-menu input.toggle");t.forEach(e=>{e.addEventListener("change",function(){if(!this.checked){const e=this.nextElementSibling.nextElementSibling;if(e){const t=e.querySelectorAll("input.toggle:checked");t.forEach(e=>{e.checked=!1})}}})});const e=document.querySelector(".book-brand a");e&&e.addEventListener("click",function(e){e.preventDefault();const t=this.getAttribute("href");window.location.href=t})})